@hungry = ['Hungry','Hungry','Hungry','Craving','Famished','Got The Munchies','Hankering','Insatiate','On An Empty Stomach','Rapacious','Ravenous','Starved','Unsatisfied','Voracious','Yearning']

def make_bar
  rand <= 0.5 ? "The #{@streets.sample}" : "#{@streets.sample} #{@streets.sample}"
end

def menu_item
  if rand <= 0.2
    # three-ingredient menu item
    if rand <= 0.5
      "#{make_food}, #{make_food} & #{make_food}"
    else
      "#{make_food} with #{make_food} & #{make_food}"
    end
  elsif rand <= 0.4
    # two-ingredient menu item
    if rand <= 0.5
      "#{make_food} & #{make_food}"
    else
      "#{make_food} with #{make_food}"
    end
  else
    # one-ingredient menu item
    make_food
  end
end

def make_food
  food = ""
  food += @adjectives.sample + " " if rand <= 0.55
  food += @foods.sample
  food += " " + @food_shapes.sample
  food.strip
end

def make_price
  ((rand * 13)+8).floor
end

reply "#{@hungry.sample}? Try the #{menu_item} at #{make_bar}. $#{make_price}"
